    public static void checkBirthday(CustomerDto customer) throws MyException1 {
        Field[] fields = customer.getClass().getDeclaredFields();
        for(Field f: fields) {
            if(f.isAnnotationPresent(Adulthood.class)) {
                f.setAccessible(true);
                try {
                    LocalDate fValue = (LocalDate) f.get(customer);
                    LocalDate now = LocalDate.now();
                    if(now.getYear()-fValue.getYear()<18) {
                        throw new MyException1(fValue);
                    }
                } catch(IllegalAccessException e) {
                    e.printStackTrace();
                }
            }
        } 
    }

    
    public static void checkDiscountRate(CustomerDto customer) throws MyException2 {
        Field[] fields = customer.getClass().getDeclaredFields();
        for(Field f: fields) {
            if(f.isAnnotationPresent(Min.class)) {
                f.setAccessible(true);
                Min annotation = f.getAnnotation(Min.class);
                int minValue = annotation.value();
                try {
                    int fValue = (int) f.get(customer);
                    if(fValue<minValue) {
                        throw new MyException2(fValue);
                    }
                } catch(IllegalAccessException e) {
                    e.printStackTrace();
                }
            }
            if(f.isAnnotationPresent(Max.class)) {
                f.setAccessible(true);
                Max annotation = f.getAnnotation(Max.class);
                int maxValue = annotation.value();
                try {
                    int fValue = (int) f.get(customer);
                    if(fValue>maxValue) {
                        throw new MyException2(fValue);
                    }
                } catch(IllegalAccessException e) {
                    e.printStackTrace();
                }
            }
        } 
    }
        
